		$('.index_wap_country_main_country').slideDown('300');
	}
})
$('.index_wap_currency_main_sure').click(function(){
	if($(this).hasClass('show')){
		$(this).removeClass('show');
		$('.index_wap_country_main_currency').slideUp('300');
	}else{
		$(this).addClass('show');
